import styled from "styled-components";

export const HeaderContainer = styled.div`
    margin-bottom: 60px;
    display: flex;
    justify-content: center;
    border: 3px solid #fff;
    border-padding: 10px;
    align-items: center;
    

    @media screen and (max-width: 960px) {
        margin-bottom: 30px;
    }
`

export const Header = styled.h1`
    margin-left: 20px;
    margin-right: 20px;
    color: #fff;
    text-align: center;
    @media screen and (max-width: 960px) {
        font-size: 15px;
    }
`
